import React from "react";
import { makeStyles } from "@mui/styles";
import { Box, Typography } from "@mui/material";

const useStyles = makeStyles((theme) => ({
  root: {
    [theme.breakpoints.up("md")]: {
      width: "100%",
      height: "400px",
      backgroundColor: theme.palette.secondary.main,
      display: "flex"
    }
  },
  container: {
    [theme.breakpoints.up("md")]: {
      width: "60%",
      margin: "auto",
      justifyContent: "center",
      textAlign: "center"
    },
    "& > img": {
      width: "76px",
      height: "78px"
    }
  },
  title: {
    color: theme.palette.primary.main
  },
  description: {
    paddingTop: theme.spacing(3),
    color: theme.palette.primary.main
  }
}))

const Summary = () => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Box className={classes.container}>
        <img src="/images/Summary-logo.png"></img>

        <Typography variant="h1" className={classes.title}>
          How Stag DAO Works
        </Typography>

        <Typography variant="h1" className={classes.subtitle}>
          Revenue Generation
        </Typography>
        <Typography variant="subtitle1" className={classes.description}>
          Stag DAO will allow users to bond new STAG through purchasing STAG with existing assets.
        </Typography>

        <Typography variant="h1" className={classes.subtitle}>
          Grow Treasury & Minimize Price Impacts
        </Typography>
        <Typography variant="subtitle1" className={classes.description}>
          By building a deep treasury with ELK liquidity pairs, Stag DAO will minimize price impacts and strengthen the ElkNet and Stag DAO.
        </Typography>
        <Typography variant="subtitle1" className={classes.description}>
          Stag takes the innovative bonding concept and gives it actual utility by using it build to depth of liquidity for Summary liquidity pairs, initially native network tokens (e.g. Summary-MATIC), which are key for minimizing price impact on SummaryNet's cross-chain liquidity protocol. This creates a mutually beneficial flywheel effect. As Stag grows, Summary gets stronger; as Summary grows, Stag gets stronger.
        </Typography>
      </Box>
    </div>
  )
}

export default Summary;
